/* ------------------------Lexical Rules Section---------------------- */

<YYINITIAL> {
  "\\begin" {IGNORE}* "{axdef}"
        {
          yybegin(ZED);
          assert ! addSpace_;
          begin("axdef");
          inAxiomaticBox_ = true;
          return result(ZString.AX);
        }
  "\\begin" {IGNORE}* "{gendef}"
        {
          yybegin(ZED);
          assert ! addSpace_;
          begin("gendef");
          inAxiomaticBox_ = true;
          return result(ZString.GENAX);
        }

  "\\begin" {IGNORE}* "[disabled]" "{schema}" {IGNORE}* "{" / {NAME} "}" {IGNORE}* "["
        {
          yybegin(ZED);
          assert ! addSpace_;
          begin("schema");
          braceStack_.push(BraceType.NAME);
          return result(ZString.SCHCHAR + ZString.GENCHAR + ZEvesString.DISABLEDDEFTAG);
        }

  "\\begin" {IGNORE}* "{schema}" {IGNORE}* "{" / {NAME} "}" {IGNORE}* "["
        {
          yybegin(ZED);
          assert ! addSpace_;
          begin("schema");
          braceStack_.push(BraceType.NAME);
          return result(ZString.SCHCHAR + ZString.GENCHAR);
        }

  "\\begin" {IGNORE}* "[disabled]" "{schema}" {IGNORE}* "{"
        {
          yybegin(ZED);
          assert ! addSpace_;
          begin("schema");
          braceStack_.push(BraceType.NAME);
          return result(ZString.SCH + ZEvesString.DISABLEDDEFTAG);
        }

  "\\begin" {IGNORE}* "{schema}" {IGNORE}* "{"
        {
          yybegin(ZED);
          assert ! addSpace_;
          begin("schema");
          braceStack_.push(BraceType.NAME);
          return result(ZString.SCH);
        }
  "\\begin" {IGNORE}* "{schema}" {IGNORE}*
        {
          yybegin(ERROR);
        }

  "\\begin" {IGNORE}* "[disabled]" "{zed}"
        {
          yybegin(ZED);
          assert ! addSpace_;
          begin("zed");
          return result(ZString.ZED + ZEvesString.DISABLEDDEFTAG);
        }

  "\\begin" {IGNORE}* "{zed}"
        {
          yybegin(ZED);
          assert ! addSpace_;
          begin("zed");
          return result(ZString.ZED);
        }
  "\\begin" {IGNORE}* "{zsection}"
        {
          yybegin(ZED);
          assert ! addSpace_;
          begin("zsection");
          return result(LatexSym.SECT, ZString.ZED);
        }

  "\\begin" {IGNORE}* "[disabled]" {IGNORE}* "{" / "theorem}"
        {
          yybegin(ZED);
          assert ! addSpace_;
          begin("theorem");
          braceStack_.push(BraceType.NAME);
          return result(ZString.ZED + ZEvesString.DISABLEDTHMTAG);
        }

  "\\begin" {IGNORE}* "{" / "theorem}"
        {
          // CZT extension: pass theorem name into Unicode output.
          yybegin(ZED);
          assert ! addSpace_;
          begin("theorem");
          braceStack_.push(BraceType.NAME);
          return result(ZString.ZED);
          // leave the "theorem" to be treated as a name.
        }
/*  "\\begin" {IGNORE}* "{theorem}" "{" {NAME} "}"
        {
          // The Z standard ignores the NAME part of \begin{theorem}
          yybegin(ZED);
          assert ! addSpace_;
          begin("theorem");
          return result(ZString.ZED);
        }
*/

  "\\begin" {IGNORE}* "{zedjoker}" {IGNORE}* "{"
        {
          yybegin(ZED);
          assert ! addSpace_;
          begin("zedjoker");
          braceStack_.push(BraceType.NAME);
          return result(ZPattString.JOKER);
        }
  "\\begin" {IGNORE}* "{zedjoker}" {IGNORE}*
        {
          yybegin(ERROR);
        }
  "\\begin" {IGNORE}* "{zedrule}" {IGNORE}* "{"
        {
          yybegin(ZED);
          assert ! addSpace_;
          begin("zedrule");
          braceStack_.push(BraceType.NAME);
          return result(ZPattString.RULE);
        }
  "\\begin" {IGNORE}* "{zedrule}" {IGNORE}*
        {
          yybegin(ERROR);
        }
  "\\begin" {IGNORE}* "{zedoracle}" {IGNORE}* "{"
        {
          yybegin(ZED);
          assert ! addSpace_;
          begin("zedoracle");
          braceStack_.push(BraceType.NAME);
          return result(ZPattString.PROVISO);
        }
  "\\begin" {IGNORE}* "{zedoracle}" {IGNORE}*
        {
          yybegin(ERROR);
        }


  "\\begin" {IGNORE}* "{circusjoker}" {IGNORE}* "{"
        {
          yybegin(ZED);
          assert ! addSpace_;
          begin("circusjoker");
          braceStack_.push(BraceType.NAME);
