// Treat everything within this state as comments  
<CIRCUSTOOLSOFF> {
  "\\circtoolson"
       {
         yybegin(YYINITIAL);
       }
  "\\circustoolsoff"
       {
         yybegin(YYINITIAL);
         reportError(ErrorType.ERROR,
                     CircusParseMessage.MSG_UNMATCHED_CIRCUSTOOLS_OFF,
                     yytext(), null);
       }
  {NL}
       {
         // do nothing
         //
         //String result = addSpace();
         //result += yytext();
         //return result(result);
       }
  ({LETTER} | [0-9])*
        {
          // do nothing
         //
         //String result = addSpace();
         //result += yytext();
         //return result(result);
        }
  .
        {
          // do nothing
         //
         //String result = addSpace();
         //result += yytext();
         //return result(result);
        }
}

