import net.sourceforge.czt.zpatt.jaxb.JaxbXmlWriter;


/**
 * A parser for LaTeX mark-up.  This is a convenience class that
 * composes the {@link LatexScanner} and the {@link Parser}.
 *
 * @see net.sourceforge.czt.parser.z
 */
public class 
{
  private Parser parser_;

  public (Source source,
                  SectionInfo sectInfo,
                  Properties properties)
    throws IOException
  {
    LatexScanner scanner = new LatexScanner(source, sectInfo, properties);
    parser_ = new Parser(scanner, source, sectInfo);
  }

  /**
   * Converts latex to zml.
   */
  public static void main(String[] args)
  {
    String usage = "Usage: ."
      + " [ -in <inputfile>] [ -out <outputfile>]";
    try {
      String filename = null;
      Logger logger = CztLogger.getLogger(.class);
      Reader in = new InputStreamReader(System.in);
      OutputStream out = System.out;
      for (int i = 0; i < args.length; i++) {
        if ("-in".equals(args[i])) {
          if (i < args.length) {
            filename = args[++i];
            in = new InputStreamReader(new FileInputStream(filename));
          } else {
            System.err.println(usage);
            return;
          }
        } else if ("-out".equals(args[i])) {
          if (i < args.length) {
            out = new FileOutputStream(args[++i]);
          } else {
            System.err.println(usage);
            return;
          }
        } else {
          System.err.println(usage);
          return;
        }
      }
      final Source source = new FileSource(filename);
      source.setMarkup(Markup.LATEX);
       parser =
        new (source, new SectionManager(), new Properties());
      Term term = parser.parse();
      if (term != null) {
        XmlWriter xmlWriter = new JaxbXmlWriter();
        xmlWriter.write(term, out);
      }
      else {
        System.err.println("Parse error");
      }
      out.close();
    } catch (Exception e) {
      e.printStackTrace();
    }
  }

  public Term parse()
    throws ParseException
  {
    try {
      final Symbol parseTree = parser_.parse();
      final Term term = (Term) parseTree.value;
      return term;
    }
    catch (ParseException e) {
      throw e;
    }
    catch (RuntimeException e) {
      throw e;
    }
    catch (Exception e) {
      e.printStackTrace();
      throw new CztException("This should never happen", e);
    }
  }
}
