      return result;
    }

    private ZSect updateZSect(ZSect zSect, OpTable opTable, ThmTable thmTable
      , JokerTable jokerTable 
      , ProofTable proofTable)
    {
      final boolean parseError = parser.hasErrors();
      final String sectName = zSect.getName();
      final PrecedenceHandlingVisitor visitor =
        new PrecedenceHandlingVisitor(opTable);
      final ZSect sect = (ZSect) zSect.accept(visitor);
      LatexMarkupFunction latexMarkupFunction = null;
      final Key<LatexMarkupFunction> key = new Key<LatexMarkupFunction>(sectName, LatexMarkupFunction.class);
      if (sectInfo_.isCached(key)) {
        try {
          latexMarkupFunction = sectInfo_.get(key);
        }
        catch (CommandException exception) {
          String message =
            "Error retrieving LatexMarkupFunction for " + sectName;
          CztLogger.getLogger(.class).warning(message);
        }
      }


      if (sectName.equals("zeves_toolkit"))
      {
        // adds applies$to definitions to top of sections Z para list
        parserState_.updateZSectWithAddedPara(sect);
      }


      // Retrieves the markup directives through the markup function
      // from the section manager. The markup function calculates the
      // LaTeX markup directives within a section. When no directives
      // are added, this paragraph is added to the front of the paragraph
      // list of the section with an empty list of  directives.
      if (latexMarkupFunction != null) {
        final LatexMarkupPara markupPara = latexMarkupFunction.toAst(factory_);
        ZUtils.assertZParaList(sect.getParaList()).add(0, markupPara);
      }

      // Add implicit process paragraphs to the ZSect term.
      ZUtils.assertZParaList(sect.getParaList()).addAll(parserState_.getImplicitlyDeclProcPara());
//      System.out.println("Printing " + sect.getPara().size() + " paragraphs from " +
//         sect.getName() + ".");
//      for(Para para : sect.getPara()) {
//        System.out.println("\t" + para.toString());
//      }
//      System.out.println();



      if (parseError) return sect;

      Set dependencies = new HashSet();
      for (Parent parent : sect.getParent()) {
        dependencies.add(new Key(parent.getWord(), OpTable.class));
      }

      Set thmdependencies = new HashSet();
      for (Parent parent : sect.getParent()) {
        thmdependencies.add(new Key(parent.getWord(), ThmTable.class));
      }

      sectInfo_.put(new Key<ZSect>(sectName, ZSect.class), sect, dependencies);
      sectInfo_.put(new Key<OpTable>(sectName, OpTable.class), opTable, dependencies);
      sectInfo_.put(new Key<ThmTable>(sectName, ThmTable.class), thmTable, thmdependencies);

      sectInfo_.put(new Key<JokerTable>(sectName, JokerTable.class), jokerTable, dependencies);


      Set proofdependencies = new HashSet();
      for (Parent parent : sect.getParent()) {
        proofdependencies.add(new Key(parent.getWord(), ProofTable.class));
      }
      proofTable.checkAgainst(thmTable); // just warning in case of inconsistency (e.g., more proofs than conj para).
      //proofTable.verifyConsistency(thmTable); -- for error
      sectInfo_.put(new Key<ProofTable>(sectName, ProofTable.class), proofTable, proofdependencies);

      return sect;
    }

