        :}
        |
        inner_term:lhs INTERLEAVE inner_term:rhs
        {:
           RESULT = factory_.createInterleaveProExpr(opExpr(lhs), opExpr(rhs));
           addLocAnn(RESULT, lhsleft, lhsright);
