package ;

import java.util.Map;

public class SymMap
  extends net.sourceforge.czt.parser.z.SymMap
{
  private static Map<String,Integer> MAP =
    net.sourceforge.czt.parser.z.SymMap.createMap(Sym.class);
  private static SymMap SYM_MAP = new SymMap();

  public static SymMap getInstance()
  {
    return SYM_MAP;
  }

  protected SymMap()
  {
  }

  public Integer get(String symbolName)
  {
    return MAP.get(symbolName);
  }
}
