/**
 * <p>A parser for unicode.  This class is a convenience class
 * that assembles the {@link UnicodeScanner} and the {@link Parser}.
 *
 * @author Petra Malik
 * @see net.sourceforge.czt.parser.z
 */
public class 
{
  Parser parser_;

  public (Source source, SectionInfo sectInfo, Properties properties)
    throws IOException
  {
    Scanner scanner = new UnicodeScanner(source, properties);
    parser_ = new Parser(scanner, source, sectInfo);
  }

  public Term parse()
    throws ParseException
  {
    try {
      Symbol parseTree = parser_.parse();
      return (Term) parseTree.value;
    }
    catch (ParseException e) {
      throw e;
    }
    catch (Exception e) {
      throw new RuntimeException("This should never happen", e);
    }
  }
}
