scanner:
package ;

import java.io.*;
import java.util.HashMap;
import java.util.Map;

import java_cup.runtime.Scanner;
import java_cup.runtime.Symbol;

import net.sourceforge.czt.parser.util.CztScanner;
import net.sourceforge.czt.parser.util.LatexMarkupParser;
import net.sourceforge.czt.session.SectionManager;

/**
 * <p>
 *   The high-level latex to unicode converter for Object Z.
 * </p>
 * <p>
 *   This class is responsible for translating
 *   Object Z
 *   specifications written in the latex mark-up language into unicode.
 *   It provides tokens (instances of class {@link Symbol}) whos values
 *   are unicode strings and which contain line and column number
 *   information of the original latex file or stream.  This makes it
 *   possible to use this class as a standalone translater and, via
 *   {@link net.sourceforge.czt.parser.util.CztReader},
 *   as the input to the unicode scanner and therefore
 *   for the Object Z parser.
 * </p>
 */
public class 
  implements CztScanner
{
  private Map markupFunction_;
  private Scanner scanner_;
  private SectionManager manager_;

  public (String filename, SectionManager manager)
    throws FileNotFoundException
  {
    markupFunction_ = new HashMap();
    Latex2Unicode l2u = new Latex2Unicode(filename, markupFunction_);
    scanner_ = new LatexMarkupParser(l2u, manager, markupFunction_);
  }

  public (Reader in, SectionManager manager)
  {
    markupFunction_ = new HashMap();
    Latex2Unicode l2u = new Latex2Unicode(in);
    l2u.setMarkupFunction(markupFunction_);
    scanner_ = new LatexMarkupParser(l2u, manager, markupFunction_);
  }

  public (InputStream in, SectionManager manager)
  {
    this(new InputStreamReader(in), manager);
  }

  public void reset(String filename)
    throws FileNotFoundException
  {
    Map markupFunction = new HashMap();
    Latex2Unicode l2u = new Latex2Unicode(filename, markupFunction);
    markupFunction_ = markupFunction;
    scanner_ = new LatexMarkupParser(l2u, manager_, markupFunction_);
  }

  /**
   * Returns the latex mark-up function.
   */
  public Map getMarkupFunction()
  {
    return markupFunction_;
  }

  public Symbol next_token()
    throws Exception
  {
    return scanner_.next_token();
  }

  public static void main(String[] args)
  {
    String usage = "Usage: java ."
      + " [ -in <inputfile>] [ -out <outputfile>]";
    try {
      InputStream input = System.in;
      Writer writer = new PrintWriter(System.out);
      for (int i = 0; i < args.length; i++) {
        if ("-in".equals(args[i])) {
          if (i < args.length) {
            input = new FileInputStream(args[++i]);
          } else {
            System.err.println(usage);
            return;
          }
        } else if ("-out".equals(args[i])) {
          if (i < args.length) {
            writer =
              new OutputStreamWriter(new FileOutputStream(args[++i]), "UTF-8");
          } else {
            System.err.println(usage);
            return;
          }
        } else {
          System.err.println(usage);
          return;
        }
      }
       scanner = new (input, new SectionManager());
      Symbol s = null;
      while ( (s = scanner.next_token()).sym != Sym.EOF) {
        if (s.value != null) writer.write((String) s.value);
      }
      writer.close();
    } catch (Exception e) {
      e.printStackTrace();
    }
  }
}
