    private LocInfo getLocation(List<LocString> list)
    {
      if (list != null && list.size() > 0) {
        if (list.size() == 1) return list.get(0).getLocation();
        return getLocation(list.get(0).getLocation(),
                           list.get(list.size() - 1).getLocation());
      }
      return null;
    }


    /**
