     protected void resetIsOpExpr()
     {
       isOpExpr_.clear();
     } 

    /**
     * Return the top of the isOpExpr_ stack.
     */
     protected boolean isOpExpr()
     {
       if (isOpExpr_.size() == 0) {
         return false;
       }
       return isOpExpr_.peek();
     } 

    /**
