parser:
package ;

import java.io.*;
import java.util.logging.Logger;

import java_cup.runtime.Scanner;
import java_cup.runtime.Symbol;

import net.sourceforge.czt.base.ast.Term;
import net.sourceforge.czt.base.util.AstValidator;
import net.sourceforge.czt.base.util.XmlWriter;
import net.sourceforge.czt.parser.util.ParentNotFoundException;
import net.sourceforge.czt.util.CztLogger;
import net.sourceforge.czt.util.ParseException;

import net.sourceforge.czt.oz.jaxb.JaxbValidator;
import net.sourceforge.czt.oz.jaxb.JaxbXmlWriter;


import net.sourceforge.czt.z.jaxb.JaxbValidator;
import net.sourceforge.czt.z.jaxb.JaxbXmlWriter;

import net.sourceforge.czt.parser.util.OperatorTable;
import net.sourceforge.czt.session.SectionManager;

/**
 * An unicode parser.
 */
public class 
{
  Parser parser_;

  public (Reader in, OperatorTable table, SectionManager manager)
  {
    Scanner scanner = new SmartScanner(new UnicodeScanner(in));
    parser_ = new Parser(scanner, table, manager);
  }

  public (Reader in, OperatorTable table, String filename,
                  SectionManager manager)
  {
    Scanner scanner = new SmartScanner(new UnicodeScanner(in));
    parser_ = new Parser(scanner, table, filename, manager);
  }

  /**
   * Converts unicode to zml.
   */
  public static void main(String[] args)
  {
    String usage = "Usage: ."
      + " [ -in <inputfile>] [ -out <outputfile>]";
    try {
      String filename = null;
      Logger logger = CztLogger.getLogger(.class);
      Reader in = new InputStreamReader(System.in);
      OutputStream out = System.out;
      for (int i = 0; i < args.length; i++) {
        if ("-in".equals(args[i])) {
          if (i < args.length) {
            filename = args[++i];
            in = new InputStreamReader(new FileInputStream(filename));
          } else {
            System.err.println(usage);
            return;
          }
        } else if ("-out".equals(args[i])) {
          if (i < args.length) {
            out = new FileOutputStream(args[++i]);
          } else {
            System.err.println(usage);
            return;
          }
        } else {
          System.err.println(usage);
          return;
        }
      }
      OperatorTable table = new OperatorTable();
       parser =
        new (in, table, filename, new SectionManager());
      Term term = parser.parse();
      AstValidator validator = new JaxbValidator();
      if (! validator.validate(term)) {
        String message = "AST is not valid.";
        logger.warning(message);
      }
      XmlWriter xmlWriter = new JaxbXmlWriter();
      xmlWriter.write(term, out);
      out.close();
    } catch (Exception e) {
      e.printStackTrace();
    }
  }

  public Term parse()
    throws ParseException, ParentNotFoundException
  {
    try {
      Symbol parseTree = parser_.parse();
      return (Term) parseTree.value;
    }
    catch (ParseException e) {
      throw e;
    }
    catch (ParentNotFoundException e) {
      throw e;
    }
    catch (Exception e) {
      throw new RuntimeException("This should never happen", e);
    }
  }
}
